package com.replit.homework;

public class ArithmeticOperation11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/* Write a program, to print the perimeter and area of a rectangle with width =5 and the height =8.
 * Output:
 * The perimeter of a rectangle with width__ and height__is equals to__and the area equals to__
 */
		
		long width=5;
		long height=8;
		long number=2;
		long number1=width*number;
		long number2=height*number;
		long multiple=width*height;
		long perimeter=number1+number2;
		System.out.println("The perimeter of a rectangle with width " +width+ " and height " +height+ " is equals to " +perimeter+ " and the area equals to " +multiple );
	}

}
