		/* Write a java program to add, subtract, multiply and divide 2 decimal values.
		 * Your program should say "the___ of 2 numbers ___ and ___ is equal to ___"
		 */
	
		double number1=20.3;
		double number2=10.3;
		double sum=number1+number2;
